import React, { useState, useEffect } from "react";
import Monitor from "./Monitor";
import './Machine.css';
import { Velocity } from '../../../Services/Velocity';

function Machine(prop) {
  const [showPopup, setShowPopup] = useState(false); // State variable for pop-up visibility
  const [apiData, setApiData] = useState([]); // State variable for API data
  const [machineData, setMachineData] = useState([]); // State variable for machine data

  useEffect(() => {
    // Function to fetch API data
    const fetchData = async () => {
      try {
        const response = await Velocity();
        setApiData(response);
      } catch (error) {
        console.log(error);
      }
    };

    // Fetch API data initially
    fetchData();

    // Fetch API data every 30 seconds
    const interval = setInterval(fetchData, 30000);

    // Clean up the interval when the component is unmounted
    return () => clearInterval(interval);
  }, []);

  const getVelocity = async (id) => {
    const response = await Velocity();
    const neededData = response.filter((ele) => ele.id === id);
    setApiData(neededData);
  };

  const handleInfoClick = async (id) => {
    await getVelocity(id);
    setShowPopup(true);
  };

  const closePopup = () => {
    setShowPopup(false);
  };

  useEffect(() => {
    setMachineData(prop.machine);
  }, [prop.machine]);

  return (
    <div className="machines">
      {machineData.map((ele) => (
        <div className="machine" key={ele.id}>
          <div>
            <h4 style={{ color: "black", backgroundColor: "rgb(255, 193, 7)" }}>{ele.name}</h4>
            <div className="signals">
              <Monitor monitor={ele.monitors} />
              <h4>HS {ele.healthScore}</h4>
              <div className="info-icon" onClick={() => handleInfoClick(ele.id)}>
                &#x1F6C8;
              </div>
            </div>
          </div>
        </div>
      ))}
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h2>{apiData.name}</h2>
            <div className="tableWrapper">
              <table>
                <tr>
                  <th>Monitor Name</th>
                  <th>Axi</th>
                  <th>Ver</th>
                  <th>Hor</th>
                  <th>tem</th>
                  <th>HS</th>
                  <th>Vibration Trend</th>
                </tr>
                {apiData.monitors.map((data) => (
                  <tr key={data.id}>
                    <td>{data.name}</td>
                    <td>{data.velocityX}</td>
                    <td>{data.velocityY}</td>
                    <td>{data.velocityZ}</td>
                    <td>{data.temperature}</td>
                    <td>{data.healthScore}%</td>
                    <td>{data.trend}</td>
                  </tr>
                ))}
              </table>
            </div>
            <center>
              <a href="https://idap.infinite-uptime.com/#/dashboard/MonitoringTable" target="_blank" rel="noopener noreferrer">For Detailed Analysis Click Here!</a>
            </center>
            <div>
              <b>Observation:</b> {apiData.observation}
              <br /><br />
              <b>Diagnostic:</b> {apiData.diagnostic}
              <br /><br />
              <b>Recommendation:</b> {apiData.recommendation}
              <br /><br />
            </div>
            <button className="close-btn" onClick={closePopup}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Machine;
