import React, { useState, useEffect } from "react";
import './AA.css';
// import { LoginCredentialsAndQueries } from "../../../Services/LoginCredentialsAndQueries";
import { LoginCredentialsAndQueries } from "../../../Services/loginCredentialsAndQueries";

function AA(prop) {
    const [accessToken, setAccessToken] = useState("");
    let params = "";
    const currentPlant = prop.currentPlant;
    useEffect(() => {
        const fetchDataz = async () => {
            try {
                const response = await LoginCredentialsAndQueries();
                if (response.length > 0) {
                    console.log("responseAA" + response);
                    setAccessToken(response);
                    //  params=response.queryParams
                }
            } catch (error) {
                console.error(error);
            }
        };

        fetchDataz();
    }, []);

    console.log("AA1" + currentPlant[0].plantid, accessToken);
    let plantid = currentPlant[0].plantid;
    useEffect(() => {
        const fetchData = async () => {
            try {
                let now = new Date();
                let dtTo = now.toISOString().replace("T", " ").replace("Z", "");
                let dtFrom = now.getHours() - 48;
                now.setHours(dtFrom);
                dtFrom = now.toISOString().replace("T", " ").replace("Z", "");


                const plantsResponse = await fetch(`https://api-idap.infinite-uptime.com/api/3.0/idap-api/plants/${orgid}/machine-group-stats`, {
                    method: 'GET',
                    headers: {
                        'accept': 'application/json',
                        'Authorization': 'Bearer ' + accessToken,
                    },
                });
                plantsData = await plantsResponse.json();

            } catch (error) {
                console.error(error);
            }
        };

        fetchData();
    }, []);
    return (
        <div>
            <div className="head">
                Anomaly Alert
            </div>
            <div className="content">
                list elements
            </div>
        </div>
    )
}
export default AA;
