export async function Velocity() {
    let accessToken = "";

    const arrayOfMachines = [];



    try {

        const loginResponse = await window.ZOHO.CREATOR.init().then(function (data) {
            let accessTokenz = "";


            var config = {
                appName: "infinite-control-room",
                reportName: "All_Users",
                criteria: "Username ==\"rapaka.chandrashekar@gmail.com\"",
                page: "1",
                pageSize: "100"
            }
            const access = window.ZOHO.CREATOR.API.getAllRecords(config).then(function (response) {
                let userData = response;

                accessTokenz = userData.data[0].Access_Token;
                //   console.log("userData"+JSON.stringify(userData.data[0].Access_Token));
                // console.log("check"+accessToken);
                // return accessToken;
                return accessTokenz;
            });
            return access;
        });
        const queryParams = await window.ZOHO.CREATOR.init().then(function (data) {
            return window.ZOHO.CREATOR.UTIL.getQueryParams();
        });

        const loginData = await loginResponse;
        // console.log("loginData"+ loginData);
        accessToken = loginData;
        //accessToken="eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICIyY0NxRnpxRXJrNU5GS1RjTi1YSk1IdE9NS2tWVTZXS1hIdHZFMF8xZE5ZIn0..l8NL7t1Kqf77hOHo_kGSpcKzDoSjFFYmKdyn3y6-fC45B_ICvvYz_FLMfazPMOxVzMG0nb0nD88y_J45wFYTjLnfwbZbIzw--vbVKt1EMe4qK6ko4wv1jTRrjg97m7PaQL3FLcv1JSPafJ8ukPm1z4BPo0tpoHlbZX1vCGwm1ZgdJqTBM6bJrtrLyB1EcABWsXIbd7r_756vua7sOO_7ZkAppAiHTV3qqjM5N0cjHaKTkMXYAiVkl6kBpsZf7N8CCdwkZTKV3rapK_zQGJhS2VxSNkeiERF2Y16nyPxSjxyzQFBB6I0AB2xqSSWlhJ6Shk2yaaI1MB9_cdt8oOgJPg";
        console.log("queryParams" + JSON.stringify(queryParams));

        //console.log("accessToken"+accessToken);
        //console.log("check"+ accessToken);

        // let orgid=queryParams.PlantId.split(",")[1];
        const orgidAll = queryParams.PlantId.split(",");
        let plantsData;
        let plantsResponse
        plantsResponse = await Promise.all(orgidAll.map(async (orgid) => {
            plantsResponse = await fetch(`https://api-idap.infinite-uptime.com/api/2.0/md/monitoring/bymonitors?plantId=${orgid}`, {
                method: 'GET',
                headers: {
                    'accept': 'application/json',
                    'Authorization': 'Bearer ' + accessToken,
                },
            });
            plantsData = await plantsResponse.json();
            //   console.log("plantsdatavelocity",  plantsData.machineGroups);
            plantsData.machineGroups.map((mg) => {
                //console.log("plantsdata", mg.machines);
                mg.machines.map((machine) => {
                    arrayOfMachines.push(machine);
                })

            })


        }));

        console.log("velocityarrayofMachines", arrayOfMachines);

        return arrayOfMachines;


    } catch (error) {
        console.error(error);
        throw error;
    }
}
