import React, { useState } from "react";
import "./Pagination.css";

function Pagination({ items, currentPage, itemsPerPage, onPageChange }) {
  const [currentPlant, setCurrentPlant] = useState(null); // Add this line

  const totalPages = Math.ceil(items.length / itemsPerPage);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);

    if (pageNumber === Math.ceil(currentPlant.machineGroups.length / itemsPerPage)) {
      const currentPlantIndex = plantsData.findIndex((plant) => plant.id === currentPlant.id);
      setCurrentPlant(plantsData[currentPlantIndex + 1]);
    }
  };

  useEffect(() => {
    const interval = setInterval(() => {
      if (currentPlant) {
        const totalPages = Math.ceil(currentPlant.machineGroups.length / itemsPerPage);
        setCurrentPage((prevPage) => {
          const nextPage = (prevPage % totalPages) + 1;
          if (nextPage === 1) {
            const currentPlantIndex = plantsData.findIndex((plant) => plant.id === currentPlant.id);
            setCurrentPlant(plantsData[currentPlantIndex + 1]);
          }
          return nextPage;
        });
      }
    }, timeIn);

    return () => clearInterval(interval);
  }, [currentPlant, itemsPerPage, timeIn]);

  const handleClick = (pageNumber) => {
    onPageChange(pageNumber);
  };

  const renderPaginationItems = () => {
    const paginationItems = [];
    for (let i = 1; i <= totalPages; i++) {
      paginationItems.push(
        <li
          key={i}
          className={`pagination-item ${i === currentPage ? "active" : ""}`}
          onClick={() => handleClick(i)}
        >
          {i}
        </li>
      );
    }
    return paginationItems;
  };

  return <ul className="pagination">{renderPaginationItems()}</ul>;
}

export default Pagination;
