          let accessTokenz="";
        
        
         var config = {
          appName: "infinite-control-room",
          reportName: "All_Users",
          criteria: "Username ==\"rapaka.chandrashekar@gmail.com\"",
          page: "1",
          pageSize: "100"
        }
        const access=window.ZOHO.CREATOR.API.getAllRecords(config).then(function (response) {
          let userData = response;
          
