import React, { useState } from "react";
import Monitor from "./Monitor";
import './Machine.css';

function Machine(prop) {
  const [showPopup, setShowPopup] = useState(false); // State variable for pop-up visibility
  const [apiData, setApiData] = useState(null); // State variable for API data

  console.log(prop.machine);

  const handleInfoClick = (data) => {
    setShowPopup(true);
    setApiData(data);
  };

  return (
    <div className="machines">
      {prop.machine.map(function (ele) {
        return (
          <div className="machine" key={ele.id}>
            <div
              style={{
                border: "2px solid yellow",
                borderRadius: "10px",
                margin: "5px",
                padding: "5px"
              }}
            >
              <h4 style={{ color: "black" }}>{ele.name}</h4>
              <div className="signals">
                <Monitor monitor={ele.monitors} />
                <h4>HS {ele.healthScore}</h4>
                <div className="info-icon" onClick={() => handleInfoClick(ele.apiData)}>
                  &#x1F6C8;
                </div>
              </div>
            </div>
          </div>
        );
      })}
      {showPopup && (
        <div className="popup">
          {/* Display the API data inside the pop-up */}
          <pre>{JSON.stringify(apiData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default Machine;
