import React, { useState, useEffect } from "react";
import "./Header.css";
import KPI from "./KPI";
import { Image } from "../../Services/image";
import { LoginCredentialsAndQueries } from "../../Services/loginCredentialsAndQueries";

function Header(prop) {
  const [imageUrl, setImageUrl] = useState("");
  const [kipobj, setKipobj] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDataz = async () => {
      try {
        const response = await Image();
        if (response.length > 0) {
          console.log("imageurl", response);
          setImageUrl(response);
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchDataz();
  }, []);

  useEffect(() => {
    const fetchPlantDetails = async () => {
      try {
        if (prop.currentPlant.length > 0) {
          let plantid = prop.currentPlant[0].plantid;
          let token = await LoginCredentialsAndQueries();

          const kpidatafinal = await fetch(
            "https://api-idap.infinite-uptime.com/api/3.0/idap-api/service-requests/analytics?plantIds=" +
              plantid,
            {
              method: "GET",
              headers: {
                Authorization: "Bearer " + token,
                accept: "*/*",
              },
            }
          );
          let kpidata = await kpidatafinal.json();
          console.log("downtime" + JSON.stringify(kpidata.data[0]));

          const updatedKipobj = {
            kpi4: {
              ...kipobj.kpi4,
              title: JSON.stringify(kpidata.data[0].completedCount),
            },
            kpi5: {
              ...kipobj.kpi5,
              title: JSON.stringify(kpidata.data[0].downtime),
            },
            kpi2: {
              ...kipobj.kpi2,
              title: JSON.stringify(prop.kpimachines),
            },
            kpi3: {
              ...kipobj.kpi3,
              title: JSON.stringify(kpidata.data[0].newCount),
            },
            kpi1: {
              ...kipobj.kpi1,
              title: JSON.stringify(prop.kpimonitors),
            },
          };

          setKipobj(updatedKipobj);
          setLoading(false);
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchPlantDetails();
  }, [prop.currentPlant, prop]);

  return (
    <div className="kpis">
      <img src={imageUrl} alt="Plant" />
      {loading ? (
        <div>Loading...</div>
      ) : (
        Object.keys(kipobj).map(function (ele) {
          return <KPI data={kipobj[ele]} />;
        })
      )}
    </div>
  );
}

export default Header;
