export async function LoginCredentialsAndQueries() {
    try {
      const initData = await window.ZOHO.CREATOR.init();
  
      const loginResponse = initData.then(function (data) {
        var config = {
          appName: "infinite-control-room",
          reportName: "All_Users",
          criteria: "Username ==\"rapaka.chandrashekar@gmail.com\"",
          page: "1",
          pageSize: "100",
        };
  
        const access = window.ZOHO.CREATOR.API.getAllRecords(config);
        return Promise.all([access, initData]);
      });
  
      const [accessResponse, initDataResponse] = await loginResponse;
      const userData = accessResponse.data;
      const accessToken = userData[0]?.Access_Token;
  
      console.log("userData", accessToken);
      console.log("queryParamsLOGIN", JSON.stringify(initDataResponse));
  
      return accessToken;
    } catch (error) {
      console.error(error);
      throw error;
    }
  }
